mapper.select:
select * from history


        and model_name like concat('%',#{model},'%')


        and task_type = #{type}


        and client = #{client}


        and submit_time >= #{createTimeBegin}


        and submit_time <= #{createTimeEnd}

    and username=#{username}

order by history_id DESC

mapper.update:
update history
set history_name=#{param2}
where history_id=#{param1}
